import { React, Component } from 'react';
import { Container, Row, Col} from 'reactstrap';
import Navi from './layouts/navbar/navi';
import ProductList from './layouts/Main/ProductList';
import CategoriesList from './layouts/Main/CategoriesList';

export default class App extends Component {
  state = {
    products: [],
    currentCategory: '',
  };

  changeCategory = (category) => {
    this.setState({ currentCategory: category.name });
  };

  componentDidMount() {
    this.getProducts();
  }

  getProducts = category_id => {
    let url = "http://localhost:3000/products";
    if(category_id)
    {
      url += "?category_id=" + category_id;
    }
    fetch(url)
      .then((response) => response.json())
      .then((data) => this.setState({ products: data }));
  };

  render() {
    let titlecategory = "Category List";
    let titleproductlist = "Product List";
    return (
      <>
        <Navi />
        <Container>
          <Row>
            <Col xs="3">
              <CategoriesList changeCategory={this.changeCategory} title={titlecategory}/>
            </Col>
            <Col xs="9">
              <ProductList title={titleproductlist} currentCategory={this.state.currentCategory} Products={this.state.products}/>
            </Col>
          </Row>
        </Container>
      </>
    )
  }
}
