import React, { Component } from 'react';
import { Table, Button, Pagination, PaginationLink, PaginationItem } from "reactstrap";

export default class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentPage: 1,
      itemsPerPage: 5
    };
  }

  handleClick = (pageNumber) => {
    this.setState({ currentPage: pageNumber });
  }

  renderPageNumbers() {
    const { products } = this.props;
    const { itemsPerPage, currentPage } = this.state;
    const pageNumbers = Math.ceil(products.length / itemsPerPage);

    return (
      Array.from({ length: pageNumbers }).map((_, index) => (
        <PaginationItem key={index} active={index + 1 === currentPage}>
          <PaginationLink href="#" onClick={() => this.handleClick(index + 1)}>
            {index + 1}
          </PaginationLink>
        </PaginationItem>
      ))
    );
  }

  render() {
    const { products } = this.props;
    const { currentPage, itemsPerPage } = this.state;
    const indexOfLastItem = currentPage * itemsPerPage;
    const indexOfFirstItem = indexOfLastItem - itemsPerPage;
    const currentItems = products.slice(indexOfFirstItem, indexOfLastItem);

    return (
      <>
        <h3>{this.props.title} - {this.props.currentCategory}</h3>
        <Table>
          <thead>
            <tr>
              <th>#</th>
              <th>Product Name</th>
              <th>Category</th>
              <th>Price</th>
              <th>Quantity</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {
              currentItems.map((product) => (
                <tr key={product.id}>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>{product.category}</td>
                  <td>{product.price}</td>
                  <td>{product.quantity}</td>
                  <td onClick={() => this.props.AddtoCart(product.name)}>
                    <Button color="info">Add to Cart</Button>
                  </td>
                </tr>
              ))
            }
          </tbody>
        </Table>
        <Pagination>
          <PaginationItem>
            <PaginationLink first href="#" />
          </PaginationItem>
          <PaginationItem>
            <PaginationLink previous href="#" />
          </PaginationItem>
          {this.renderPageNumbers()}
          <PaginationItem>
            <PaginationLink next href="#" />
          </PaginationItem>
          <PaginationItem>
            <PaginationLink last href="#" />
          </PaginationItem>
        </Pagination>
      </>
    );
  }
}
